import { Controller, SubmitHandler, useForm } from "react-hook-form";
import { InferType, number, object } from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import {
  FormErrorMessage,
  FormLabel,
  FormControl,
  //   Input,
  NumberInput,
  NumberInputField,
  //   Button,
} from "@chakra-ui/react";

const schema = object({
  depositAmount: number().required(),
});

type FormFields = InferType<typeof schema>;

const onSubmit: SubmitHandler<FormFields> = (data) => {
  console.log("Data: ", data);
};

const TermDepositCalc = () => {
  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm<FormFields>({
    defaultValues: {
        depositAmount: 100
    },
    resolver: yupResolver(schema),
  });

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <Controller
        control={control}
        name="depositAmount"
        render={({ field: { onChange, value } }) => (
          <FormControl isInvalid={!!errors?.depositAmount}>
            <FormLabel htmlFor="depositAmount">Deposit Amount</FormLabel>

            <NumberInput onChange={onChange} value={value} precision={0} defaultValue={100}>
              <NumberInputField />
            </NumberInput>

            <FormErrorMessage>
              {errors.depositAmount && errors.depositAmount.message?.toString()}
            </FormErrorMessage>
          </FormControl>
        )}
      />

