import { Controller, useForm } from "react-hook-form";
import { InferType, number, object, string } from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import {
  FormErrorMessage,
  FormLabel,
  FormControl,
  //   Input,
  NumberInput,
  NumberInputField,
  Select,
  //   Button,
} from "@chakra-ui/react";
import { useEffect, useState } from "react";

import { interestPaidFrequency } from "./fixtures/investmentTerms";

const schema = object({
  depositAmount: number().required(),
  interestRate: number().required(),
  investmentTerm: number().required(),
  interestPaidFrequency: string().matches(/monthly | quarterly | annually | atMaturity/)
});

type FormFields = InferType<typeof schema>;

// const calculateCompoundInterest;

const TermDepositCalc = () => {
    // const [totalBalance, setTotalBalance ] = useState()

  const {
    control,
    formState: { errors },
    watch
  } = useForm<FormFields>({
    defaultValues: {
      depositAmount: 100,
    },
    resolver: yupResolver(schema),
  });

//   const [ depositAmount ] = watch(['depositAmount'])

//   useEffect(() => console.log('val: ', depositAmount), [depositAmount])

  return (
    <form>
      <Controller
        control={control}
        name="depositAmount"
        render={({ field: { onChange, value } }) => (
          <FormControl isInvalid={!!errors?.depositAmount}>
            <FormLabel htmlFor="depositAmount">Deposit Amount</FormLabel>

            <NumberInput onChange={onChange} value={value} precision={0}>
              <NumberInputField />
            </NumberInput>

            {/* <FormErrorMessage>
              {errors.depositAmount && errors.depositAmount.message?.toString()}
            </FormErrorMessage> */}
          </FormControl>
        )}
      />

      <Controller
        control={control}
        name="interestRate"
        render={({ field: { onChange, value } }) => (
          <FormControl isInvalid={!!errors?.interestRate}>
            <FormLabel htmlFor="interestRate">Interest Rate</FormLabel>

            <NumberInput onChange={onChange} value={value} precision={2}>
              <NumberInputField />
            </NumberInput>

            {/* <FormErrorMessage>
              {errors.interestRate && errors.interestRate.message?.toString()}
            </FormErrorMessage> */}
          </FormControl>
        )}
      />

      <Controller
        control={control}
        name="investmentTerm"
        render={({ field: { onChange, value } }) => (
          <FormControl isInvalid={!!errors?.investmentTerm}>
            <FormLabel htmlFor="investmentTerm">Investment Term</FormLabel>

            <NumberInput onChange={onChange} value={value} precision={0}>
              <NumberInputField />
            </NumberInput>

            {/* <FormErrorMessage>
              {errors.investmentTerm &&
                errors.investmentTerm.message?.toString()}
            </FormErrorMessage> */}
          </FormControl>
        )}
      />

      <Controller
        control={control}
        name="interestPaidFrequency"
        render={({ field: { onChange, value } }) => (
          <FormControl isInvalid={!!errors?.investmentTerm}>
            <FormLabel htmlFor="interestPaidFrequency">Interest Paid</FormLabel>

            <Select
              placeholder="Select option"
              onChange={onChange}
              value={value}
            >
              {interestPaidFrequency.map((interestPaid) => (
                <option value={interestPaid.periods} key={interestPaid.name}>
                  {interestPaid.name}
                </option>
              ))}
            </Select>

            {/* <FormErrorMessage>
              {errors.interestPaidFrequency && errors.interestPaidFrequency.message?.toString()}
            </FormErrorMessage> */}
          </FormControl>
        )}
      />
    </form>
  );
};

export { TermDepositCalc };
