const calculateSimpleInterest = (
  depositAmount: number,
  interestRate: number,
  investmentTerm: number
) => {
  const totalBalance =
    depositAmount * (1 + (interestRate / 100) * (investmentTerm / 12));
  return totalBalance;
};

export { calculateSimpleInterest };
