import { Controller, useForm } from "react-hook-form";
import { InferType, number, object, string } from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import {
  FormErrorMessage,
  FormLabel,
  FormControl,
  //   Input,
  NumberInput,
  NumberInputField,
  Select,
  //   Button,
} from "@chakra-ui/react";
import { useEffect, useState } from "react";

import { interestPaidFrequencies } from "./fixtures/investmentTerms";

const schema = object({
  depositAmount: number().required(),
  interestRate: number().required(),
  investmentTerm: number().required(),
  interestPaidFrequency: number().required(),
});

type FormFields = InferType<typeof schema>;

const calculateCompoundInterest = (
  depositAmount: number,
  interestRate: number,
  investmentTerm: number,
  interestPaidFrequency: number
) => {
  //  ((P*(1+i)^n) - P)
  const totalBalance =
    depositAmount *
    (1 + interestRate / 100 / interestPaidFrequency) **
      (interestPaidFrequency * investmentTerm);
  return totalBalance;
};

const TermDepositCalc = () => {
  const [totalBalance, setTotalBalance ] = useState(0)

  const {
    control,
    formState: { errors },
    watch,
  } = useForm<FormFields>({
    defaultValues: {
      depositAmount: 100,
      interestRate: 5,
      investmentTerm: 2,
      interestPaidFrequency: interestPaidFrequencies[0].frequency,
    },
    resolver: yupResolver(schema),
  });

  const [depositAmount, interestRate, investmentTerm, interestPaidFrequency] =
    watch([
      "depositAmount",
      "interestRate",
      "investmentTerm",
      "interestPaidFrequency",
    ]);

  useEffect(() => {
    // const totalBalance = calculateCompoundInterest(10000, 1.1, 3, 12);
    const totalBalance = calculateCompoundInterest(
      depositAmount,
      interestRate,
      investmentTerm,
      interestPaidFrequency
    );

    setTotalBalance(totalBalance)
    console.log("total: ", totalBalance);
  }, [depositAmount, interestRate, investmentTerm, interestPaidFrequency]);

  return (
    <>
    <form>
      <Controller
        control={control}
        name="depositAmount"
        render={({ field: { onChange, value } }) => (
          <FormControl isInvalid={!!errors?.depositAmount}>
            <FormLabel htmlFor="depositAmount">Deposit Amount</FormLabel>

            <NumberInput onChange={onChange} value={value} precision={0}>
              <NumberInputField />
            </NumberInput>

            {/* <FormErrorMessage>
              {errors.depositAmount && errors.depositAmount.message?.toString()}
            </FormErrorMessage> */}
          </FormControl>
        )}
      />

      <Controller
        control={control}
        name="interestRate"
        render={({ field: { onChange, value } }) => (
          <FormControl isInvalid={!!errors?.interestRate}>
            <FormLabel htmlFor="interestRate">Interest Rate</FormLabel>

            <NumberInput onChange={onChange} value={value} precision={2}>
              <NumberInputField />
            </NumberInput>

            {/* <FormErrorMessage>
              {errors.interestRate && errors.interestRate.message?.toString()}
            </FormErrorMessage> */}
          </FormControl>
        )}
      />

      <Controller
        control={control}
        name="investmentTerm"
        render={({ field: { onChange, value } }) => (
          <FormControl isInvalid={!!errors?.investmentTerm}>
            <FormLabel htmlFor="investmentTerm">Investment Term</FormLabel>

            <NumberInput onChange={onChange} value={value} precision={0}>
              <NumberInputField />
            </NumberInput>

            {/* <FormErrorMessage>
              {errors.investmentTerm &&
                errors.investmentTerm.message?.toString()}
            </FormErrorMessage> */}
          </FormControl>
        )}
      />

      <Controller
        control={control}
        name="interestPaidFrequency"
        render={({ field: { onChange, value } }) => (
          <FormControl isInvalid={!!errors?.investmentTerm}>
            <FormLabel htmlFor="interestPaidFrequency">Interest Paid</FormLabel>

            <Select
              placeholder="Select option"
              onChange={onChange}
              value={value}
            >
              {interestPaidFrequencies.map((interestPaid) => (
                <option value={interestPaid.frequency} key={interestPaid.name}>
                  {interestPaid.name}
                </option>
              ))}
            </Select>

            {/* <FormErrorMessage>
              {errors.interestPaidFrequency && errors.interestPaidFrequency.message?.toString()}
            </FormErrorMessage> */}
          </FormControl>
        )}
      />
    </form>
    <div>{totalBalance}</div>
    </>
  );
};

export { TermDepositCalc };
