import { Controller, SubmitHandler, useForm } from "react-hook-form";
import { InferType, number, object } from "yup";
import { yupResolver } from "@hookform/resolvers/yup";
import {
  FormErrorMessage,
  FormLabel,
  FormControl,
  //   Input,
  NumberInput,
  NumberInputField,
  //   Button,
} from "@chakra-ui/react";

const schema = object({
  depositAmount: number().required(),
  interestRate: number().required(),
  investmentTerm: number().required(),
});

type FormFields = InferType<typeof schema>;

const onSubmit: SubmitHandler<FormFields> = (data) => {
  console.log("Data: ", data);
};

const TermDepositCalc = () => {
  const {
    control,
    handleSubmit,
    formState: { errors },
  } = useForm<FormFields>({
    defaultValues: {
      depositAmount: 100,
    },
    resolver: yupResolver(schema),
  });

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <Controller
        control={control}
        name="depositAmount"
        render={({ field: { onChange, value } }) => (
          <FormControl isInvalid={!!errors?.depositAmount}>
            <FormLabel htmlFor="depositAmount">Deposit Amount</FormLabel>

            <NumberInput onChange={onChange} value={value} precision={0}>
              <NumberInputField />
            </NumberInput>

            <FormErrorMessage>
              {errors.depositAmount && errors.depositAmount.message?.toString()}
            </FormErrorMessage>
          </FormControl>
        )}
      />

      <Controller
        control={control}
        name="interestRate"
        render={({ field: { onChange, value } }) => (
          <FormControl isInvalid={!!errors?.interestRate}>
            <FormLabel htmlFor="interestRate">Interest Rate</FormLabel>

            <NumberInput onChange={onChange} value={value} precision={2}>
              <NumberInputField />
            </NumberInput>

            <FormErrorMessage>
              {errors.interestRate && errors.interestRate.message?.toString()}
            </FormErrorMessage>
          </FormControl>
        )}
      />

      <Controller
        control={control}
        name="investmentTerm"
        render={({ field: { onChange, value } }) => (
          <FormControl isInvalid={!!errors?.investmentTerm}>
            <FormLabel htmlFor="investmentTerm">Investment Term</FormLabel>

            <NumberInput onChange={onChange} value={value} precision={0}>
              <NumberInputField />
            </NumberInput>

            <FormErrorMessage>
              {errors.investmentTerm && errors.investmentTerm.message?.toString()}
            </FormErrorMessage>
          </FormControl>
        )}
      />
      <button type="submit">Submit</button>
    </form>
  );
};

export { TermDepositCalc };
