import { SubmitHandler, useForm } from "react-hook-form";
import {
  FormErrorMessage,
  FormLabel,
  FormControl,
  Input,
  Button,
} from "@chakra-ui/react";

type FormFields = {
    depositAmount: number;
    interest: number;
};
const onSubmit: SubmitHandler<FormFields> = (data) => {
  console.log("Data: ", data);
};

const TermDepositCalc = () => {
  const {
    handleSubmit,
    register,
    formState: { errors, isSubmitting },
  } = useForm<FormFields>();

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <FormControl isInvalid={!!errors?.depositAmount}>
        <FormLabel htmlFor="depositAmount">Deposit Amount</FormLabel>
        <Input
          id="depositAmount"
          placeholder="100"
        //   type="number"
          {...register("depositAmount", {
            required: "Please enter the starting deposit amount",
            // minLength: { value: 4, message: "Minimum length should be 4" },
          })}
        />
        <FormErrorMessage>
          {errors.depositAmount && errors.depositAmount.message?.toString()}
        </FormErrorMessage>
      </FormControl>

      <FormControl isInvalid={!!errors?.depositAmount}>
        <FormLabel htmlFor="depositAmount">Deposit Amount</FormLabel>
        <Input
          id="depositAmount"
          placeholder="100"
          type="number"
          {...register("depositAmount", {
            required: "Please enter the starting deposit amount",
            // minLength: { value: 4, message: "Minimum length should be 4" },
          })}
        />
        <FormErrorMessage>
          {errors.depositAmount && errors.depositAmount.message?.toString()}
        </FormErrorMessage>
      </FormControl>
      {/* <button type="submit">Submit</button> */}
    </form>
  );
};

export { TermDepositCalc };
